pub enum SynthesisMode {

    /// Indicate to the `ConstraintSystem` that it should only generate
    /// constraint matrices and not populate the variable assignments.
    Setup,

    /// Indicate to the `ConstraintSystem` that it populate the variable
    /// assignments. If additionally `construct_matrices == true`, then generate
    /// the matrices as in the `Setup` case.
    Prove {
        /// If `construct_matrices == true`, then generate
        /// the matrices as in the `Setup` case.
        construct_matrices: bool,
    },
}

pub enum ConstraintSystemRef<F: Field> {

    /// Represents the case where we *don't* need to allocate variables or enforce
    /// constraints. Encountered when operating over constant values.
    None,

    /// Represents the case where we *do* allocate variables or enforce constraints.
    CS(Rc<RefCell<ConstraintSystem<F>>>),
}

pub enum SynthesisError {

    /// During synthesis, we tried to allocate a variable when `ConstraintSystemRef`
    /// was `None`.
    MissingCS,

    /// During synthesis, we lacked knowledge of a variable assignment.
    AssignmentMissing,

    /// During synthesis, we divided by zero.
    DivisionByZero,

    /// During synthesis, we constructed an unsatisfiable constraint system.
    Unsatisfiable,

    /// During synthesis, our polynomials ended up being too high of degree
    PolynomialDegreeTooLarge,

    /// During proof generation, we encountered an identity in the CRS
    UnexpectedIdentity,

    /// During verification, our verifying key was malformed.
    MalformedVerifyingKey,

    /// During CRS generation, we observed an unconstrained auxiliary variable
    UnconstrainedVariable,
}

pub enum TracingMode {

    /// Instructs `ConstraintLayer` to filter out any spans that *do not* have `target == "r1cs"`.
    OnlyConstraints,

    /// Instructs `ConstraintLayer` to filter out any spans that *do* have `target == "r1cs"`.
    NoConstraints,

    /// Instructs `ConstraintLayer` to not filter out any spans.
    All,
}

pub enum Variable {

    /// Represents the "zero" constant.
    Zero,

    /// Represents of the "one" constant.
    One,

    /// Represents a public instance variable.
    Instance(usize),

    /// Represents a private witness variable.
    Witness(usize),

    /// Represents of a linear combination.
    SymbolicLc(LcIndex),
}
